import React from "react";
import ChatBotify,{ChatBotProvider,
     useMessages,
     useFlow,} from "react-chatbotify";

function ChatBotWidget() {
  return (
    <ChatBotify
      settings={{
        theme: {
          color: "#4e8cff"
        },
        startMessages: [
          {
            type: "text",
            content: "¡Hola! ¿Eres cliente nuestro?"
          },
          {
            type: "option",
            options: [
              {
                label: "Sí",
                value: "yes",
                trigger: "askName"
              },
              {
                label: "No",
                value: "no",
                trigger: "endConversation"
              }
            ]
          }
        ],
        flow: {
          askName: {
            message: "¿Cuál es tu nombre?",
            user: true,
            trigger: "askSurname"
          },
          askSurname: {
            message: "¿Cuál es tu apellido?",
            user: true,
            trigger: "askEmail"
          },
          askEmail: {
            message: "¿Cuál es tu correo electrónico?",
            user: true,
            trigger: "askPhone"
          },
          askPhone: {
            message: "¿Cuál es tu número de teléfono?",
            user: true,
            trigger: "askCompany"
          },
          askCompany: {
            message: "¿Cuál es el nombre de tu empresa?",
            user: true,
            trigger: "selectService"
          },
          selectService: {
            message: "Selecciona un servicio:",
            options: [
              { label: "Servicio A", value: "A", trigger: "askQuantity" },
              { label: "Servicio B", value: "B", trigger: "askQuantity" },
              { label: "Servicio C", value: "C", trigger: "askQuantity" }
            ]
          },
          askQuantity: {
            message: "¿Qué cantidad deseas?",
            user: true,
            trigger: "addAnother"
          },
          addAnother: {
            message: "¿Quieres agregar otro servicio?",
            options: [
              { label: "Sí", value: "yes", trigger: "selectService" },
              { label: "No", value: "no", trigger: "endSummary" }
            ]
          },
          endSummary: {
            message: "Gracias por tu información. ¡Te contactaremos pronto!"
          },
          endConversation: {
            message: "Gracias por tu interés. ¡Que tengas buen día!"
          }
        }
      }}
    />
  );
}

export default ChatBotWidget;
