import React,{useEffect, useState} from 'react';
import { Widget, addResponseMessage } from 'react-chat-widget-react-18';
import 'react-chat-widget-react-18/lib/styles.css';

function App() {
  const [step, setStep] = React.useState(0);
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const hasGreeted = React.useRef(false);

React.useEffect(() => {
  if (!hasGreeted.current) {
    addResponseMessage('¡Hola! ¿En qué puedo ayudarte?');
    addResponseMessage("¿Cuál es tu nombre?");
    hasGreeted.current = true;
  }
}, []);

  const handleNewUserMessage = (newMessage) => {
    console.log(`Mensaje recibido: ${newMessage}`);
    // Aquí podrías hacer una llamada a tu backend o API.
    addResponseMessage('Recibí tu mensaje, gracias.');
  };

  return (
    <div className="App">
      <h1>Mi App con Chat</h1>
      <Widget
        handleNewUserMessage={handleNewUserMessage}
        title="Chat Soporte"
        subtitle="Estamos para ayudarte"
      />
    </div>
  );
}

export default App;
