import React, {useEffect} from "react";
import ChatBotify,{ChatBotProvider,
     useMessages,
     useFlow,} from "react-chatbotify";

     function ChatLogic() {
          const { pushMessage, pushOptions } = useMessages();
          const { setFlow } = useFlow();

          useEffect(() => {
            setFlow({
              start: async () => {
                pushMessage("¡Hola! ¿Eres cliente nuestro?");
                pushOptions([
                  {
                    label: "Sí",
                    value: "yes",
                    trigger: "askName",
                  },
                  {
                    label: "No",
                    value: "no",
                    trigger: "endConversation",
                  },
                ]);
              },
              askName: async () => {
                pushMessage("¿Cuál es tu nombre?");
              },
              askSurname: async () => {
                pushMessage("¿Cuál es tu apellido?");
              },
              askEmail: async () => {
                pushMessage("¿Cuál es tu correo electrónico?");
              },
              askPhone: async () => {
                pushMessage("¿Cuál es tu número de teléfono?");
              },
              askCompany: async () => {
                pushMessage("¿Cuál es el nombre de tu empresa?");
              },
              selectService: async () => {
                pushMessage("Selecciona un servicio:");
                pushOptions([
                  { label: "Servicio A", value: "A", trigger: "askQuantity" },
                  { label: "Servicio B", value: "B", trigger: "askQuantity" },
                  { label: "Servicio C", value: "C", trigger: "askQuantity" },
                ]);
              },
              askQuantity: async () => {
                pushMessage("¿Qué cantidad deseas?");
              },
              addAnother: async () => {
                pushMessage("¿Quieres agregar otro servicio?");
                pushOptions([
                  { label: "Sí", value: "yes", trigger: "selectService" },
                  { label: "No", value: "no", trigger: "endSummary" },
                ]);
              },
              endSummary: async () => {
                pushMessage("Gracias por tu información. ¡Te contactaremos pronto!");
              },
              endConversation: async () => {
                pushMessage("Gracias por tu interés. ¡Que tengas buen día!");
              },
            });
            // Iniciar flujo
    setTimeout(() => {
     setFlow("start");
   }, 500);
 }, [setFlow, pushMessage, pushOptions]);

 return null;
}

function ChatBotWidget() {
  return (
     <ChatBotProvider>
     <ChatLogic />
   </ChatBotProvider>
  );
}

export default ChatBotWidget;
