import React from 'react';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';

const steps = [
  {
    id: 'intro',
    message: '¿Eres cliente nuestro?',
    trigger: 'cliente',
  },
  {
    id: 'cliente',
    options: [
      { value: 'si', label: 'Sí', trigger: 'preguntarNombre' },
      { value: 'no', label: 'No', trigger: 'fin' },
    ],
  },
  {
    id: 'fin',
    message: 'Gracias por tu interés. ¡Hasta pronto!',
    end: true,
  },
  {
    id: 'preguntarNombre',
    message: '¿Cuál es tu nombre?',
    trigger: 'nombre',
  },
  {
    id: 'nombre',
    user: true,
    trigger: 'apellido',
  },
  {
    id: 'apellido',
    message: '¿Y tu apellido?',
    trigger: 'apellidoInput',
  },
  {
    id: 'apellidoInput',
    user: true,
    trigger: 'correo',
  },
  {
    id: 'correo',
    message: '¿Cuál es tu correo electrónico?',
    trigger: 'correoInput',
  },
  {
    id: 'correoInput',
    user: true,
    trigger: 'telefono',
  },
  {
    id: 'telefono',
    message: '¿Cuál es tu número telefónico?',
    trigger: 'telefonoInput',
  },
  {
    id: 'telefonoInput',
    user: true,
    trigger: 'empresa',
  },
  {
    id: 'empresa',
    message: '¿Cuál es el nombre de tu empresa?',
    trigger: 'empresaInput',
  },
  {
    id: 'empresaInput',
    user: true,
    trigger: 'servicio',
  },
  {
    id: 'servicio',
    message: '¿Qué servicio deseas?',
    trigger: 'servicioInput',
  },
  {
    id: 'servicioInput',
    user: true,
    trigger: 'cantidad',
  },
  {
    id: 'cantidad',
    message: '¿Cuántas unidades deseas?',
    trigger: 'cantidadInput',
  },
  {
    id: 'cantidadInput',
    user: true,
    trigger: 'agregarOtro',
  },
  {
    id: 'agregarOtro',
    message: '¿Deseas agregar otro servicio?',
    trigger: 'agregarOtroDecision',
  },
  {
    id: 'agregarOtroDecision',
    options: [
      { value: 'si', label: 'Sí', trigger: 'servicio' },
      { value: 'no', label: 'No', trigger: 'final' },
    ],
  },
  {
    id: 'final',
    message: '¡Gracias por tu información! Pronto nos pondremos en contacto.',
    end: true,
  },
];

const theme = {
  background: '#f5f8fb',
  headerBgColor: '#0a74da',
  headerFontColor: '#fff',
  headerFontSize: '16px',
  botBubbleColor: '#0a74da',
  botFontColor: '#fff',
  userBubbleColor: '#fff',
  userFontColor: '#4a4a4a',
};

const ChatBotWidget = () => {
  return (
    <ThemeProvider theme={theme}>
      <ChatBot
        steps={steps}
        headerTitle="Asistente Virtual"
        floating={true}
      />
    </ThemeProvider>
  );
};

export default ChatBotWidget;
