// ChatBotWidget.jsx
import React from "react";
import { ChatBotProvider, useChatHistory } from "react-chatbotify";
//import "react-chatbotify/dist/style.css";

const CustomBot = () => {
  const { messages, append } = useChatHistory();

  const [step, setStep] = React.useState(0);

  React.useEffect(() => {
    if (step === 0) {
      append({
        type: "text",
        content: "¿Eres cliente nuestro?",
        options: [
          {
            label: "Sí",
            value: "yes",
            onClick: () => setStep(1),
          },
          {
            label: "No",
            value: "no",
            onClick: () => setStep(-1),
          },
        ],
      });
    }

    if (step === -1) {
      append({ type: "text", content: "Gracias, hasta pronto." });
    }

    if (step === 1) {
      append({ type: "text", content: "¿Cuál es tu nombre?" });
      setStep(2);
    }

    if (step === 2 && messages.at(-1)?.type === "user") {
      append({ type: "text", content: "¿Cuál es tu apellido?" });
      setStep(3);
    }

    if (step === 3 && messages.at(-1)?.type === "user") {
      append({ type: "text", content: "¿Cuál es tu correo electrónico?" });
      setStep(4);
    }

    if (step === 4 && messages.at(-1)?.type === "user") {
      append({ type: "text", content: "¿Cuál es tu número de teléfono?" });
      setStep(5);
    }

    if (step === 5 && messages.at(-1)?.type === "user") {
      append({ type: "text", content: "¿Cuál es el nombre de tu empresa?" });
      setStep(6);
    }

    if (step === 6 && messages.at(-1)?.type === "user") {
      append({
        type: "text",
        content: "Selecciona un servicio:",
        options: [
          {
            label: "Servicio A",
            value: "a",
            onClick: () => setStep(7),
          },
          {
            label: "Servicio B",
            value: "b",
            onClick: () => setStep(7),
          },
        ],
      });
    }

    if (step === 7) {
      append({ type: "text", content: "¿Qué cantidad deseas?" });
      setStep(8);
    }

    if (step === 8 && messages.at(-1)?.type === "user") {
      append({
        type: "text",
        content: "¿Deseas agregar otro servicio?",
        options: [
          {
            label: "Sí",
            value: "yes",
            onClick: () => setStep(6),
          },
          {
            label: "No",
            value: "no",
            onClick: () => setStep(9),
          },
        ],
      });
    }

    if (step === 9) {
      append({ type: "text", content: "Gracias por tu información. ¡Nos pondremos en contacto!" });
      setStep(10); // para evitar reejecución
    }
  }, [messages, step, append]);

  return null;
};

export default function ChatBotWidget() {
  return (
    <ChatBotProvider>
      <CustomBot />
    </ChatBotProvider>
  );
}
