export function actionManager(callback = (action) => { }) {
    let currentAction = null;
    return {
        get: () => currentAction,
        set: (action) => {
            currentAction = action;
            callback(currentAction);
        },
        clear: () => {
            currentAction = null;
            callback(currentAction);
        },
    };
}
