import React,{useEffect, useState} from 'react';
import { Widget, addResponseMessage } from 'react-chat-widget-react-18';
import 'react-chat-widget-react-18/lib/styles.css';

function App() {
  const [step, setStep] = React.useState(0);
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const hasGreeted = React.useRef(false);

React.useEffect(() => {
  if (!hasGreeted.current) {
    addResponseMessage('¡Hola! ¿En qué puedo ayudarte?');
    addResponseMessage("¿Cuál es tu nombre?");
    hasGreeted.current = true;
  }
}, []);

  const handleNewUserMessage = (msg) => {
    if (step === 0) {
      setFormData({ ...formData, name: msg });
      addResponseMessage("Gracias, " + msg + ". ¿Cuál es tu correo electrónico?");
      setStep(1);
    } else if (step === 1) {
      setFormData({ ...formData, email: msg });
      addResponseMessage("Perfecto. ¿Cuál es tu mensaje?");
      setStep(2);
    } else if (step === 2) {
      setFormData({ ...formData, message: msg });
      addResponseMessage("¡Gracias por tu mensaje! Te responderemos pronto.");
      setStep(3);

      // Aquí podrías enviar los datos al backend:
      console.log("Formulario enviado:", {
        ...formData,
        message: msg,
      });
    } else {
      addResponseMessage("Ya terminamos el formulario. ¡Gracias!");
    }
  };

  return (
    <div className="App">
      <h1>Mi App con Chat</h1>
      <Widget
        handleNewUserMessage={handleNewUserMessage}
        title="Chat Soporte"
        subtitle="Estamos para ayudarte"
      />
    </div>
  );
}

export default App;
