// src/ChatBot.js
import React, { useEffect } from 'react';
import { createBot } from 'botui'; 

const ChatBot = () => {
  useEffect(() => {
    const botui = new createBot('botui-app');  // Inicializa BotUI con un contenedor específico

    // Configuración de la primera interacción
    botui.message.add({
      content: '¡Hola! ¿En qué puedo ayudarte hoy?'
    }).then(() => {
      return botui.action.text({
        action: {
          placeholder: 'Escribe tu pregunta aquí...'
        }
      });
    }).then((res) => {
      botui.message.add({
        content: `Recibí tu mensaje: "${res.text}". ¡Gracias!`
      });
    });

  }, []);

  return (
    <div id="botui-app" style={{ width: '400px', margin: '0 auto' }}></div>
  );
};

export default ChatBot;
